        tree.pack(side=tk.LEFT, fill=tk.BOTH, expand=True)
        scrollbar.pack(side=tk.RIGHT, fill=tk.Y)
        
        # Load forms data
        def refresh_forms_list():
            tree.delete(*tree.get_children())
