            # ØªÙ†Ø¸ÙŠÙ Ø£ÙŠ Ø±Ù…ÙˆØ² HTML Ù…ØªØ¨Ù‚ÙŠØ©
            safe_text = str(text).strip()
            safe_text = safe_text.replace('&', '&amp;')  # ØªØ±Ù…ÙŠØ² & Ø£ÙˆÙ„Ø§Ù‹
            safe_text = safe_text.replace('<', '&lt;')
