    def save_threat_record(self, tree, item_id, entries, editor):
        """ุญูุธ ุจูุงูุงุช ุงูุชูุฏูุฏ"""
        # ุฌูุน ุงูุจูุงูุงุช ูู ุญููู ุงูุฅุฏุฎุงู
        values = []
        for entry in entries:
            if isinstance(entry, tk.Text):
                values.append(entry.get("1.0", tk.END).strip())
            else:
                values.append(entry.get())
        
        # ุงูุชุญูู ูู ุนุฏู ูุฌูุฏ ุญููู ูุงุฑุบุฉ (ูุง ุนุฏุง ุงูููุงุญุธุงุช)
        if any(value.strip() == "" for value in values[:-1]):  # ูุง ุนุฏุง ุงูุญูู ุงูุฃุฎูุฑ (ุงูููุงุญุธุงุช)
            messagebox.showwarning("ุชุญุฐูุฑ", "ุงูุฑุฌุงุก ููุก ุฌููุน ุงูุญููู ุงููุทููุจุฉ")
            return
        
        # ุฅุถุงูุฉ ุฃู ุชุญุฏูุซ ุงูุณุฌู
        if item_id:
            tree.item(item_id, values=values)
            self.status_var.set("ุชู ุชุญุฏูุซ ุงูุชูุฏูุฏ ูู ุงูุณุฌู")
        else:
            tree.insert("", tk.END, values=values)
            self.status_var.set("ุชู ุฅุถุงูุฉ ุชูุฏูุฏ ุฌุฏูุฏ ุฅูู ุงูุณุฌู")
        
        editor.destroy()
    
