    def edit_record(self, tree, actual_form_name):
        selected = tree.selection()
        if not selected:
            messagebox.showwarning("ุชุญุฐูุฑ", "ุงูุฑุฌุงุก ุชุญุฏูุฏ ุณุฌู ูุชุนุฏููู")
            return
        self.open_record_editor(tree, actual_form_name, selected[0])
    
    def delete_record(self, tree, actual_form_name):
        selected = tree.selection()
        if not selected:
            messagebox.showwarning("ุชุญุฐูุฑ", "ุงูุฑุฌุงุก ุชุญุฏูุฏ ุณุฌู ูุญุฐูู")
            return
            
        if messagebox.askyesno("ุชุฃููุฏ ุงูุญุฐู", "ูู ุฃูุช ูุชุฃูุฏ ูู ุฑุบุจุชู ูู ุญุฐู ูุฐุง ุงูุณุฌูุ"):
            try:
                # Delete from UI
                for item in selected:
                    tree.delete(item)
                
                # Get all remaining records from the tree
                remaining_records = []
                for item in tree.get_children(''):
                    values = tree.item(item, 'values')
                    remaining_records.append(values)
                
                # Update database with remaining records
                success = self.db_manager.save_form_data(
                    form_name=actual_form_name,
                    data=remaining_records,
                    user_id=self.current_user['id']
                )

                if success:
                    # Update local forms data
                    if actual_form_name in self.forms:
                        self.forms[actual_form_name]["ุงูุจูุงูุงุช"] = remaining_records
                    
                    messagebox.showinfo("ุชู ุงูุญุฐู", "ุชู ุญุฐู ุงูุณุฌู ุจูุฌุงุญ ูุญูุธู ูู ูุงุนุฏุฉ ุงูุจูุงูุงุช")
                    self.status_var.set(f"ุชู ุญุฐู ุงูุณุฌู ูู {actual_form_name} ูุญูุธู ูู ูุงุนุฏุฉ ุงูุจูุงูุงุช")
                else:
                    messagebox.showerror("ุฎุทุฃ", "ุชู ุญุฐู ุงูุณุฌู ูู ุงููุงุฌูุฉ ูููู ูุดู ูู ุญูุธู ูู ูุงุนุฏุฉ ุงูุจูุงูุงุช")
                    
            except Exception as e:
                print(f"Error deleting record: {e}")
                messagebox.showerror("ุฎุทุฃ", f"ุญุฏุซ ุฎุทุฃ ุฃุซูุงุก ุงูุญุฐู: {str(e)}")
    
