    def save_form_changes(self, form_name, entries, window):
        """ุญูุธ ุงูุชุบููุฑุงุช ุนูู ุงููููุฐุฌ"""
        try:
            updated_data = {}
            for key, entry in entries.items():
                updated_data[key] = entry.get()
            
            updated_data['updated_at'] = datetime.now().isoformat()
            
            success = self.db_manager.update_form_data(form_name, updated_data, self.current_user['id'])
            if success:
                messagebox.showinfo("ูุฌุญ", "ุชู ุญูุธ ุงูุชุบููุฑุงุช ุจูุฌุงุญ")
                window.destroy()
                self.load_saved_forms()
            else:
                messagebox.showerror("ุฎุทุฃ", "ูุดู ูู ุญูุธ ุงูุชุบููุฑุงุช")
        except Exception as e:
            messagebox.showerror("ุฎุทุฃ", f"ูุดู ูู ุญูุธ ุงูุชุบููุฑุงุช: {str(e)}")

